/**
 * CC.index.optScroll;
 * *********/
;CC.index.optScroll=function (){
    function optScroll(){
        var $optHeight=0;
        for (var i = 0; i < 3; i++) {
            $optHeight+=$('.opt-item').eq(i).outerHeight();
        };
        $("#opt_box").mCustomScrollbar({
            setHeight:464, //设置高度
            theme:"minimal-dark" //设置风格
        });
    }
    optScroll();
    $(window).resize(function (){
        $("#opt_box").mCustomScrollbar("destroy");
        optScroll();
    })
}


